import React from 'react';
import '../css/slide.css';
import '../css/all.css';

export const Slide = () => {

    const sections = document.querySelectorAll("section");
    const navLi = document.querySelectorAll("nav .container ul li");
    window.onscroll = () => {
        var current = "";

        sections.forEach((section) => {
            const sectionTop = section.offsetTop;
            if (pageYOffset >= sectionTop - 60) {
                current = section.getAttribute("id");
            }
        });

        navLi.forEach((li) => {
            li.classList.remove("active");
            if (li.classList.contains(current)) {
                li.classList.add("active");
            }
        });
    };

    return (
        <div className="slide_carrousel">
            <div className="slide_container">
                <ul className="numbers">
                    <li className='left_arrow'></li>
                    <li>
                        <a className='number' href="#asso">1</a>
                    </li>
                    <li>
                        <a className='number' href="#services">2</a>
                    </li>
                    <li>
                        <a className='number' href="#contact">3</a>
                    </li>
                    <li className='right_arrow'></li>
                </ul>
                <div className="slide active" id="asso">
                    <h1>L'association</h1>
                </div>
                <div className="slide" id="services">
                    <h1>Nos services</h1>
                </div>
                <div className="slide" id="contact">
                    <h1>Nous contacter</h1>
                </div>
            </div>
        </div>
    )
}
