import React, { useState } from 'react';
import '../css/slide.css';
import '../css/all.css';


export const Slide = () => {
    const [isActive, setActive] = useState("false");
    const [isActive2, setActive2] = useState("false");
    const [isActive3, setActive3] = useState("false");

    const handleToggle = () => {
        setActive(!isActive);
    };
    const handleToggle2 = () => {
        setActive2(!isActive2);
    };
    const handleToggle3 = () => {
        setActive3(!isActive3);
    };

    return (
        <div class="slide_carrousel">
            <div className="slide_container">
                <ul className="numbers">
                    <li className='left_arrow'></li>
                    <li>
                        <a className={isActive ? "number" : "number active"} href="#asso" onClick={handleToggle}>1</a>
                    </li>
                    <li>
                        <a className={isActive ? "number" : "number active"} href="#services" onClick={handleToggle2}>2</a>
                    </li>
                    <li>
                        <a className={isActive ? "number" : "number active"} href="#contact" onClick={handleToggle3}>3</a>
                    </li>
                    <li className='right_arrow'></li>
                </ul>
                <div className="slide active" id="asso">
                    <h1>L'association</h1>
                </div>
                <div className="slide" id="services">
                    <h1>Nos services</h1>
                </div>
                <div className="slide" id="contact">
                    <h1>Nous contacter</h1>
                </div>
            </div>
        </div>
    )
}
