import React, { useState } from 'react';
import '../css/slide.css';
import '../css/all.css';

export const Slide = () => {

    const [isActive, setActive] = useState("false");

    function handleToggle(e) {
        if (e.target.className.includes('active')) {
            setActive(!isActive);
        }
        else {
            setActive(!isActive);
        }
        console.log(e.target.className);
    };

    return (
        <div className="slide_carrousel">
            <div className="slide_container">
                <ul className="numbers">
                    <li className='left_arrow'></li>
                    <li>
                        <a className={isActive ? "number" : 'number'} href="#asso" onClick={handleToggle}>1</a>
                    </li>
                    <li>
                        <a className={isActive ? "number active" : 'number'} href="#services" onClick={handleToggle}>2</a>
                    </li>
                    <li>
                        <a className={isActive ? "number active" : 'number'} href="#contact" onClick={handleToggle}>3</a>
                    </li>
                    <li className='right_arrow'></li>
                </ul>
                <div className="slide active" id="asso">
                    <h1>L'association</h1>
                </div>
                <div className="slide" id="services">
                    <h1>Nos services</h1>
                </div>
                <div className="slide" id="contact">
                    <h1>Nous contacter</h1>
                </div>
            </div>
        </div>
    )
}
